<script type="text/javascript" src="http://maps.google.com/maps/api/js?sensor=true"></script>
<script>
    $(function() {
        // Markers
        var map1 = new GMaps({
            div: '#map1',
            lat: "<?php echo $school['lat']; ?>",
            lng: "<?php echo $school['lng']; ?>",
            mapTypeControlOptions: {
                mapTypeIds : ['hybrid', 'roadmap', 'satellite', 'terrain', 'osm', 'cloudmade']
            }
        });
        <?php
            $count = 1;
            foreach($nearby_schools as $val) {
                $marker = "";
                $marker .= "map1.addMarker({";
                $marker .= "lat: {$val['lat']},";
                $marker .= "lng: {$val['lng']},";
                $marker .= "title: '".refine_school_name($val['school_name'])."',";
                if($count == 1) {
                $marker .= "animation: google.maps.Animation.BOUNCE,";
                }
                $marker .= "infoWindow: {";
                $marker .= "content: '<p>Name: ".refine_school_name($val['school_name'])."</p>'";
                $marker .= '}});';
                echo $marker;
                $count++;
            }
        ?>
        map1.addMapType('osm', {
            getTileUrl: function(coord, zoom) {
                return 'http://tile.openstreetmap.org/' + zoom + '/' + coord.x + '/' + coord.y + '.png';
            },
            tileSize: new google.maps.Size(256, 256),
            name: 'OpenStreetMap',
            maxZoom: 18
        });
        map1.addMapType('cloudmade', {
            getTileUrl: function(coord, zoom) {
                return 'http://b.tile.cloudmade.com/8ee2a50541944fb9bcedded5165f09d9/1/256/' + zoom + '/' + coord.x + '/' + coord.y + '.png';
            },
            tileSize: new google.maps.Size(256, 256),
            name: 'CloudMade',
            maxZoom: 18
        });
        map1.setMapTypeId('osm');

        $('.school-button').on('click', function() {
            var user_id = $('#school-action').data('user-id');
            if(!user_id) {
                alert("Please login as a parent to continue with this action");
                return false;
            }
        });
    });
</script>

